import React from "react";

class BucketList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            restaurant: [],
            complete: 0,
        };
    }

    comsponentDidMount() {
        fetch("/users")
        .then(res => res.json())
        .then(data => {
            this.setState({
                restaurants:data
            })
        })
        .catch(error => {
            console.log("Failure")
        });
    }

    deleteList(e, id){
        fetch("/api/todos/" + id, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json"
            }
            })
            .then(res => {
                res.json();
                this.componentDidMount();
            })
            .catch(error => {
                console.log(error);
            });
        }


    updateList(e, id){
        fetch("/api/todos/" + id, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                id: id,
                text: e.target.value,
                complete: this.state.complete
                })
            })
            .then(res => {
                this.componentDidMount();
                })
            .catch(error => {
                console.log(error);
                });
    }



    render () {
        return (
            <div>
                heres my restaurant bucket list
                <div class="input-group mb2">
                    <input
                        type="text"
                        class="form-control"
                        defaultValue={item.text}
                        onChange={e => this.updateList(e, item.id)}
                    />
                    <div className="input-group-append">
                    <button
                        className="btn btn-danger"
                        type="button"
                        onClick={e => this.deleteList(e, item.id)}
                    >
                        X
                    </button>
                    </div>
                </div>         
            </div>
        );
    }
}

export default BucketList;
