import React from "react";


class BucketList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            restaurants: [],
            complete: 0,
        };
        this.updateList = this.updateList.bind(this);
        this.deleteList = this.deleteList.bind(this);
    }

    componentDidMount() {
        fetch("/users")
        .then(res => res.json())
        .then(data => {
            this.setState({
                restaurants:data
            })
        })
        .catch(error => {
            console.log("Failure")
        });
    }

    deleteList(e, id){
        fetch("/api/todos/" + id, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json"
            }
            })
            .then(res => {
                res.json();
                this.componentDidMount();
            })
            .catch(error => {
                console.log(error);
            });
        }


    updateList(e, id){
        fetch("/api/todos/" + id, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                id: id,
                text: e.target.value,
                complete: this.state.complete
                })
            })
            .then(res => {
                this.componentDidMount();
                })
            .catch(error => {
                console.log(error);
                });
    }



    render () {
        return (
            <form>
                <div className="rows">
                    <ul>
                        {this.state.restaurants.map(item => {
                            return (
                                <li key={item.id}>
                                    <div className="bucket-container">
                                        <li className="bucket-list">selected restaurant</li>
                                        <input
                                        type="text"
                                        class="form-control"
                                        defaultValue={item.restaurants}
                                        onChange={e => this.updateList(e, item.id)}
                                        />
                                        <div>
                                        <button className="complete-btn"
                                        type="button"
                                        onClick={e => this.completeList(e, item.id)}>
                                            <i className="fas fa-check"></i>
                                        </button>
                                        <button 
                                        className="trash-btn"
                                        type="button"
                                        onClick={e => this.deleteList(e, item.id)}>
                                            Delete
                                            {/* <i className="fas fa-trash"></i> */}
                                        </button> 
                                        </div>
                                    </div>
                                </li>
                            )
                        })}
                    </ul>
                </div>
            </form>
        );
    }
}


export default BucketList;
