import React from "react";


class BucketList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            restaurants: [],
            complete: 0,
        };
    }

//     componentDidMount() {
//         fetch("/users")
//         .then(res => res.json())
//         .then(data => {
//             this.setState({
//                 restaurants:data
//             })
//         })
//         .catch(error => {
//             console.log("Failure")
//         });
//     }

    updateList(e, id){
        fetch("/users/updateBucketList" + id, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                id: id,
                text: e.target.value,
                complete: this.state.complete
                })
            })
            .then(res => {
                // this.componentDidMount();
                })
            .catch(error => {
                console.log(error);
                });
    }

    deleteList(e, id){
        fetch("/users/deleteBucketList/" + id, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json"
            }
            })
            .then(res => {
                res.json();
                // this.componentDidMount();
            })
            .catch(error => {
                console.log(error);
            });
        }

    render () {
        return (
            <form>
                <div className="bucket-container">
                    <ul>
                        {this.state.restaurants.map(item => {
                            return (
                                <li key={item.id}>
                                    <div class="input-group mb2">
                                    <input
                                        type="text"
                                        class="form-control"
                                        defaultValue={item.restaurants}
                                        onChange={e => this.updateList(e, item.id)}
                                    />
                                    <div className="input-group-append">
                                        <button
                                        className="btn btn-danger"
                                        type="button"
                                        onClick={e => this.deleteList(e, item.id)}
                                        >
                                        X
                                        </button>
                                    </div>
                                    </div>
                                </li>
                            );
                        })}
                    </ul>
                </div>
            </form>
        );
    }
}


export default BucketList;
