import React from "react";


class BucketList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            restaurants: [],
            complete: 0,
        };
        // this.updateList = this.updateList.bind(this);
        // this.deleteList = this.deleteList.bind(this);
    }

    componentDidMount() {
        fetch("/users")
        .then(res => res.json())
        .then(data => {
            this.setState({
                restaurants:data
            })
        })
        .catch(error => {
            console.log("Failure")
        });
    }

    completedList() {

    }

    deleteList(place_id){
        fetch("/users/deleteList" + place_id, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json"
            }
            })
            .then(res => {
                res.json();
                // this.componentDidMount();
            })
            .catch(error => {
                console.log(error);
            });
        }

    render () {
        return (
            <form>
                <div className="rows">
                    <ul>
                        {this.state.restaurants.map(list => {
                            return (
                                <li key={list.id}>
                                    <div className="bucket-container">
                                        <li className="bucket-list">selected restaurant</li>
                                        <input
                                        type="text"
                                        class="form-control"
                                        defaultValue={this.state.restaurants}
                                        />
                                        <div>
                                        <button className="complete-btn"
                                        type="button"
                                        onClick={e => this.completedList(e, list.place_id)}>
                                            Completed
                                            {/* <i className="fas fa-check"></i> */}
                                        </button>
                                        <button 
                                        className="trash-btn"
                                        type="button"
                                        onClick={e => this.deleteList(list.place_id)}>
                                            Delete
                                            {/* <i className="fas fa-trash"></i> */}
                                        </button> 
                                        </div>
                                    </div>
                                </li>
                            )
                        })}
                    </ul>
                </div>
            </form>
        );
    }
}


export default BucketList;
